const converter = (fromUnitDetails, toUnitDetails, currentValue, setCalculatedValue, categoryValue) => {
    
  

    const conversions = {

        "Temperature":{
            "Celsius": {"Farenheit": `(${currentValue}*(9/5)) + 32`, "Kelvin": `${currentValue} + 273.15`, "Celsius": `${currentValue}`},
            "Farenheit": {"Celsius": `(${currentValue} - 32)* (5/9)`, "Kelvin": `(${currentValue} - 32)* (5/9) + 273.15`, "Farenheit": `${currentValue}`},
            "Kelvin": {"Celsius": `${currentValue} - 273.15`, "Farenheit": `(${currentValue} - 273.15)*(9/5) + 32`, "Kelvin": `${currentValue}`}
            
        }
      
    }

    if ((fromUnitDetails === '') && (toUnitDetails === '') && (categoryValue === '')) {

    }
    else {   

        if (currentValue !== '') {
            const result = eval(conversions[categoryValue][fromUnitDetails][toUnitDetails]);
            setCalculatedValue(result);
            console.log("Result:", result);
        }
   
  

    }

       
    


}




export default converter;
