import React from 'react';
import { Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAddressBook } from '@fortawesome/free-regular-svg-icons'

const UnitCategoryOptions = ({radioValue, setRadioValue, setCurrentUnits}) => {

    const options = [
        { name: 'Temperature', value: 1, icon: faAddressBook },
        { name: 'Length', value: 2, icon: faAddressBook },
        { name: 'Area', value: 3, icon: faAddressBook },
        { name: 'Weight', value: 4, icon: faAddressBook },
        { name: 'Volume', value: 5, icon: faAddressBook },
        { name: 'Currency', value: 6, icon: faAddressBook },
        { name: 'Time', value: 7, icon: faAddressBook },
        { name: 'Speed', value: 8, icon: faAddressBook },
        { name: 'Power', value: 9, icon: faAddressBook },
        { name: 'Angle', value: 10, icon: faAddressBook },
        { name: 'Data', value: 11, icon: faAddressBook },
        { name: 'Fuel', value: 12, icon: faAddressBook },
      ];

      /* define lists here with units for each unit category */
    
      const units = {
        'Temperature': ['Celsius', 'Farenheit', 'Kelvin'],
        'Length': ['option1', 'option2', 'option3'],
        'Area': ['option1', 'option2', 'option3'],
        'Weight': ['option1', 'option2', 'option3'],
        'Volume': ['option1', 'option2', 'option3'],
        'Currency': ['option1', 'option2', 'option3'],
        'Time': ['Seconds', 'Minutes', 'Hours', 'Days', 'Weeks', 'Months', 'Years'],
        'Speed': ['option1', 'option2', 'option3'],
        'Power': ['option1', 'option2', 'option3'],
        'Angle': ['option1', 'option2', 'option3'],
        'Data': ['option1', 'option2', 'option3'],
        'Fuel': ['option1', 'option2', 'option3']

      }



    return (
        <div className='d-flex justify-content-center' style={{backgroundColor: '#D9D9D9', overflowX: 'scroll'}}>
    
            {options.map((option, idx) => (
                <UnitOption option={option} />

            ))}

    </div>
    )

};

const UnitOption = ({option}) => {
    return (
        <Col onClick={(e) => console.log(option.name)}
         xs={2} sm={2} md={2} lg={1} style={{display: "flex", justifyContent: "center", flexDirection: "column", height: "100px", backgroundColor: "#fff", border: "1px solid #000", textAlign: "center"}}>
            {option.name}
            <FontAwesomeIcon icon={option.icon} size='lg' />
        </Col>  
    )
}

export default UnitCategoryOptions;
