const converter = (fromUnitDetails, toUnitDetails, currentValue) => {
    
  

    const conversions = {
        "Celsius": {"Farenheit": `${currentValue}*1.8 + 32`, "Kelvin": `${currentValue} + 273.15`, "Celsius": `1`},
        "Farenheit": {"Celsius": `(${currentValue} - 32)* 1.8`, "Kelvin": `(${currentValue} - 32)* 1.8 + 273.15`, "Farenheit": `1`},
        "Kelvin": {"Celsius": `${currentValue} - 273.15`, "Farenheit": `(${currentValue} - 273.15)*9.5 + 32`, "Kelvin": `1`}
    
    }

    if ((fromUnitDetails === 'Celsius') && (toUnitDetails === 'Farenheit') && (currentValue !== '')){
        const result = eval(conversions[fromUnitDetails][toUnitDetails]);
        console.log("Result:", result);
        return result;

    } else {
        
    
    }

       
    


}




export default converter;
