const converter = (fromUnitDetails, toUnitDetails, currentValue) => {
    

    const result = conversions[fromUnitDetails][toUnitDetails];

    const conversions = {
        "Celsius": {"Farenheit": `${currentValue}*1.8 + 32`, "Kelvin": `${currentValue} + 273.15`},
        "Farenheit": {"Celsius": `(${currentValue} - 32)* 1.8`, "Kelvin": `(${currentValue} - 32)* 1.8 + 273.15`},
        "Kelvin": {"Celsius": `${currentValue} - 273.15`, "Farenheit": `(${currentValue} - 273.15)*9.5 + 32`}
    
    }

}




export default converter;
